import React, {useState, useEffect} from "react";
import Grid from '@mui/material/Grid';
import {styled} from '@mui/material/styles';
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import {Button, Stack} from "@mui/material";
import {patientData} from "./Data";
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import {CardActionArea, CardActions} from '@mui/material';
import "./Patient.css"
const Item = styled(Paper)(({theme}) => ({
    backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
    ...theme.typography.body2,
    padding: theme.spacing(1),
    textAlign: 'center',
    color: theme.palette.text.secondary,
}));

const PatientList = () => {
    const [search, setSearch] = useState("");
    const [filteredData, setFilteredData] = useState(patientData);
    const [showModal, setShowModal] = useState(false);
    const [editingPatient, setEditingPatient] = useState(null);
    const [newPatientData, setNewPatientData] = useState({
        name: "",
        gender: "",
        dob: "",
        image: ""
    });

    useEffect(() => {
        const results = patientData.filter(patient =>
            patient.name.toLowerCase().includes(search.toLowerCase())
        );
        setFilteredData(results);
    }, [search]);

    const handleSearch = event => {
        setSearch(event.target.value);
    };

    const handleEdit = patient => {
        setEditingPatient(patient);
        setNewPatientData(patient);
        setShowModal(true);
    };

    const handleSave = () => {
        if (editingPatient) {
            // Edit existing patient
            const updatedData = patientData.map(patient =>
                patient.id === editingPatient.id ? newPatientData : patient
            );
            setFilteredData(updatedData);
            localStorage.setItem("patientData", JSON.stringify(updatedData));
        } else {
            // Add new patient
            const newPatient = {
                id: patientData.length + 1,
                ...newPatientData
            };
            const updatedData = [...patientData, newPatient];
            setFilteredData(updatedData);
            localStorage.setItem("patientData", JSON.stringify(updatedData));
        }
        setShowModal(false);
        setEditingPatient(null);
        setNewPatientData({
            name: "",
            gender: "",
            dob: "",
            image: ""
        });
    };

    const handleCancel = () => {
        setShowModal(false);
        setEditingPatient(null);
        setNewPatientData({
            name: "",
            gender: "",
            dob: "",
            image: ""
        });
    };

    const handleInputChange = event => {
        const {name, value} = event.target;
        setNewPatientData(prevData => ({
            ...prevData,
            [name]: value
        }));
    };

    return (
        <div className="patient-list-body">
            <Stack direction={""} justifyContent="center" spacing={8}>
                <p style={{fontSize: "35px"}}>Find your patient here</p>
            </Stack>
            <div style={{display: 'flex', justifyContent: 'center', marginBottom: "100px", flexDirection: "row"}}>

                <input style={{width: "600px", height: "65px"}} type="text" value={search} onChange={handleSearch} />
            </div>
            <Box
                sx={{
                    display: 'flex',
                    flexWrap: 'wrap',
                    '& > :not(style)': {
                        m: 11,
                        width: 328,
                        height: 528,
                    },
                }}
            >
                {filteredData.map((patient) => (
             
      <Paper  >
                        <div style={{width: "328px", height: "400px"}}>
                            <img style={{width: "100%",height: "50%",}} src={patient.image} alt="green iguana" />
 </div>
                        <Typography gutterTop variant="h5">
                                     {patient.name}
                              </Typography>
                        <Stack direction={"column"}>
                                     <p>{patient.gender}</p>
                                     <p>{patient.dob}</p>
                                 </Stack>
</Paper>

//                     <Card key={patient.id} sx={{height:"600px" ,margin: "10px"}}>
//                         <CardActionArea>
// <div style={{width:"300px",height:"400px"}}>
//                             <img src={patient.image} alt="green iguana" />
// </div>
//                             <CardContent>
//                                 <Typography gutterBottom variant="h5" component="div">
//                                     {patient.name}
//                                 </Typography>
//                                 <Stack direction={"column"}>
//                                     <p>{patient.gender}</p>
//                                     <p>{patient.dob}</p>
//                                 </Stack>
//                             </CardContent>
//                         </CardActionArea>
//                         <CardActions>
//                             <Button variant="contained" onClick={() => handleEdit(patient)}>
//                                 Edit
//                             </Button>
//                         </CardActions>
//                     </Card>
                ))}
            </Box>


            {showModal && (
                <div className="modal">
                    <div className="modal-content">
                        <h2>{editingPatient ? "Edit Patient" : "Add New Patient"}</h2>
                        <form>
                            <label htmlFor="name">Name:</label>
                            <input type="text" id="name" name="name" value={newPatientData.name} onChange={handleInputChange} />
                            <label htmlFor="gender">Gender:</label>
                            <input type="text" id="gender" name="gender" value={newPatientData.gender} onChange={handleInputChange} />
                            <label htmlFor="dob">Date of Birth:</label>
                            <input type="text" id="dob" name="dob" value={newPatientData.dob} onChange={handleInputChange} />
                            <label htmlFor="image">Image URL:</label>
                            <input type="text" id="image" name="image" value={newPatientData.image} onChange={handleInputChange} />
                        </form>
                        <Button onClick={handleSave}>Save</Button>
                        <Button onClick={handleCancel}>cancel</Button>
                    </div>
                </div>
            )}
        </div>
    );
}

export default PatientList;
