import {Button, Dialog, DialogActions, DialogContent, DialogTitle, TextField} from '@mui/material';
import React, {useState} from 'react';
import {useNavigate} from 'react-router-dom';
import patientData from "././"

function Header() {
    const navigate = useNavigate();
    const [newPatientData, setNewPatientData] = useState({name: "", dob: "", gender: "", image: ""});

    const handleClick = () => {
        navigate('/patients');
    };

    const handleAddPatient = () => {
        // Add the new patient data to the JSON data
        const newData = [...data, newPatientData];
        setData(newData);

        // Close the popup
        setOpen(false);

        // Reset the new patient data state
        setNewPatientData({name: "", dob: "", gender: "", image: ""});
    };


    return (
        <div>
            <header className="header">
                <div className="brand-box">
                    <span className="brand">Shree Krishna Hospital</span>
                </div>

                <div className="text-box">
                    <h1 className="heading-primary">
                        <span className="heading-primary-main">All Entries and List of Patient</span>
                    </h1>
                    <Button
                        style={{width: "300px", height: "60px", backgroundColor: "black", color: "white", borderColor: " white", fontSize: "20px", borderRadius: "15px"}}
                        variant="outlined"
                        onClick={() => setOpen(true)}
                    >
                        Add Patient
                    </Button>

                    <Button
                        style={{width: "300px", height: "60px", backgroundColor: "black", color: "white", borderColor: " white", fontSize: "20px", borderRadius: "15px",marginLeft:"20px"}}
                        variant="outlined"
                        onClick={handleClick}
                    >
                        See Patients List
                    </Button>
                </div>
                <Dialog open={open} onClose={handleClose}>
                    <DialogTitle>Add New Patient</DialogTitle>
                    <DialogContent>
                        <DialogContentText>
                            Enter the details of the new patient below:
                        </DialogContentText>
                        <TextField
                            autoFocus
                            margin="dense"
                            label="Name"
                            fullWidth
                            value={newPatientData.name}
                            onChange={(e) => setNewPatientData({...newPatientData, name: e.target.value})}
                        />
                        <TextField
                            margin="dense"
                            label="Date of Birth"
                            fullWidth
                            value={newPatientData.dob}
                            onChange={(e) => setNewPatientData({...newPatientData, dob: e.target.value})}
                        />
                        <TextField
                            margin="dense"
                            label="Gender"
                            fullWidth
                            value={newPatientData.gender}
                            onChange={(e) => setNewPatientData({...newPatientData, gender: e.target.value})}
                        />
                        <div>
                            <input
                                type="file"
                                accept="image/*"
                                onChange={(e) => setNewPatientData({...newPatientData, image: e.target.files[0]})}
                            />
                        </div>
                    </DialogContent>
                    <DialogActions>
                        <Button onClick={handleClose} color="primary">
                            Cancel
                        </Button>
                        <Button onClick={handleAddPatient} color="primary">
                            Add
                        </Button>
                    </DialogActions>
                </Dialog>

            </header>
        </div>
    );
}

export default Header;
