import React, {useState, useEffect} from "react";
import PatientModal from "./PatientModal";

const patientData = [
    {
        id: 1,
        name: "John Doe",
        gender: "Male",
        dob: "01/01/1980",
        image: "https://randomuser.me/api/portraits/men/1.jpg"
    },
    {
        id: 2,
        name: "Jane Doe",
        gender: "Female",
        dob: "02/02/1985",
        image: "https://randomuser.me/api/portraits/women/2.jpg"
    },
    {
        id: 3,
        name: "Bob Smith",
        gender: "Male",
        dob: "03/03/1990",
        image: "https://randomuser.me/api/portraits/men/3.jpg"
    }
];


const PatientList = () => {
    const [search, setSearch] = useState("");
    const [filteredData, setFilteredData] = useState(patientData);
    const [patients, setPatients] = useState([]);
    const [filteredPatients, setFilteredPatients] = useState([]);
    const [selectedPatient, setSelectedPatient] = useState(null);
    const [searchTerm, setSearchTerm] = useState('');
    useEffect(() => {
        const results = patientData.filter(patient =>
            patient.name.toLowerCase().includes(search.toLowerCase())
        );
        setFilteredData(results);
    }, [search]);

    const handleSearch = event => {
        setSearch(event.target.value);
    };

    const handleEdit = (name, gender, dob, image) => {
        // Store the patient data in local storage
        localStorage.setItem("patient", JSON.stringify({name, gender, dob, image}));

        // Open the edit modal
        // code for opening the modal goes here
    };


    const handleSave = (updatedPatient) => {
        const index = patients.findIndex(
            (patient) => patient.id === updatedPatient.id
        );
        const updatedPatients = [...patients];
        updatedPatients[index] = updatedPatient;
        setPatients(updatedPatients);
        setFilteredPatients(updatedPatients);
        localStorage.setItem('patients', JSON.stringify(updatedPatients));
        setSelectedPatient(null);
    };


    return (
        <div>
            <input type="text" value={search} onChange={handleSearch} />
            <ul>
                {filteredData.map(patient => (
                    <li key={patient.id}>
                        <img src={patient.image} alt={patient.name} />
                        <p>{patient.name}</p>
                        <button onClick={() => handleEdit(patient.name, patient.gender, patient.dob, patient.image)}>Edit</button>
                    </li>
                ))}
            </ul>

            {selectedPatient && (
                <PatientModal patient={selectedPatient} onSave={handleSave} />
            )}
        </div>
    );
};

export default PatientList;
