import React, {useState, useEffect} from "react";
import Grid from '@mui/material/Grid';
import {styled} from '@mui/material/styles';
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import {Button} from "@mui/material";
import {patientData} from "./Data";
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import {CardActionArea, CardActions} from '@mui/material';
import "./Patient.css"
const Item = styled(Paper)(({theme}) => ({
    backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
    ...theme.typography.body2,
    padding: theme.spacing(1),
    textAlign: 'center',
    color: theme.palette.text.secondary,
}));

const PatientList = () => {
    const [search, setSearch] = useState("");
    const [filteredData, setFilteredData] = useState(patientData);
    const [showModal, setShowModal] = useState(false);
    const [editingPatient, setEditingPatient] = useState(null);
    const [newPatientData, setNewPatientData] = useState({
        name: "",
        gender: "",
        dob: "",
        image: ""
    });

    useEffect(() => {
        const results = patientData.filter(patient =>
            patient.name.toLowerCase().includes(search.toLowerCase())
        );
        setFilteredData(results);
    }, [search]);

    const handleSearch = event => {
        setSearch(event.target.value);
    };

    const handleEdit = patient => {
        setEditingPatient(patient);
        setNewPatientData(patient);
        setShowModal(true);
    };

    const handleSave = () => {
        if (editingPatient) {
            // Edit existing patient
            const updatedData = patientData.map(patient =>
                patient.id === editingPatient.id ? newPatientData : patient
            );
            setFilteredData(updatedData);
            localStorage.setItem("patientData", JSON.stringify(updatedData));
        } else {
            // Add new patient
            const newPatient = {
                id: patientData.length + 1,
                ...newPatientData
            };
            const updatedData = [...patientData, newPatient];
            setFilteredData(updatedData);
            localStorage.setItem("patientData", JSON.stringify(updatedData));
        }
        setShowModal(false);
        setEditingPatient(null);
        setNewPatientData({
            name: "",
            gender: "",
            dob: "",
            image: ""
        });
    };

    const handleCancel = () => {
        setShowModal(false);
        setEditingPatient(null);
        setNewPatientData({
            name: "",
            gender: "",
            dob: "",
            image: ""
        });
    };

    const handleInputChange = event => {
        const {name, value} = event.target;
        setNewPatientData(prevData => ({
            ...prevData,
            [name]: value
        }));
    };

    return (
        <div className="patient-list-body">

            <input type="text" value={search} onChange={handleSearch} />
            <Card sx={{maxWidth: 345}}>
                <CardActionArea>
                    <CardMedia
                        component="img"
                        height="140"
                        image="/static/images/cards/contemplative-reptile.jpg"
                        alt="green iguana"
                    />
                    <CardContent>
                        <Typography gutterBottom variant="h5" component="div">
                            Lizard
                        </Typography>
                        <Typography variant="body2" color="text.secondary">
                            Lizards are a widespread group of squamate reptiles, with over 6,000
                            species, ranging across all continents except Antarctica
                        </Typography>
                    </CardContent>
                </CardActionArea>
                <CardActions>
                    <Button size="small" color="primary">
                        Share
                    </Button>
                </CardActions>
            </Card>
            <Box sx={{flexGrow: 1}}>
                <Grid container spacing={1}>

                {filteredData.map(patient => (
                    // <li >
                        <Grid item xs={4} key={patient.id} sx={{borderColor:" red"}}>
                        <img src={patient.image} alt={patient.name} />
                        <p>{patient.name}</p>
                        <p>{patient.dob}</p>
                        <p>{patient.gender}</p>
                        <Button variant="contained" onClick={() => handleEdit(patient)}>Edit</Button>
                    {/* </li> */}
</Grid>
                ))}
</Grid>
            </Box>
            {showModal && (
                <div className="modal">
                    <div className="modal-content">
                        <h2>{editingPatient ? "Edit Patient" : "Add New Patient"}</h2>
                        <form>
                            <label htmlFor="name">Name:</label>
                            <input type="text" id="name" name="name" value={newPatientData.name} onChange={handleInputChange} />
                            <label htmlFor="gender">Gender:</label>
                            <input type="text" id="gender" name="gender" value={newPatientData.gender} onChange={handleInputChange} />
                            <label htmlFor="dob">Date of Birth:</label>
                            <input type="text" id="dob" name="dob" value={newPatientData.dob} onChange={handleInputChange} />
                            <label htmlFor="image">Image URL:</label>
                            <input type="text" id="image" name="image" value={newPatientData.image} onChange={handleInputChange} />
                        </form>
                        <Button onClick={handleSave}>Save</Button>
                        <Button onClick={handleCancel}>cancel</Button>
                    </div>
                </div>
            )}
        </div>
    );
}

export default PatientList;
