import React, {useState, useEffect} from 'react';
import PatientModal from './PatientModal';
const patientData = [
    {
        id: 1,
        name: "John Doe",
        gender: "Male",
        dob: "01/01/1980",
        image: "https://randomuser.me/api/portraits/men/1.jpg"
    },
    {
        id: 2,
        name: "Jane Doe",
        gender: "Female",
        dob: "02/02/1985",
        image: "https://randomuser.me/api/portraits/women/2.jpg"
    },
    {
        id: 3,
        name: "Bob Smith",
        gender: "Male",
        dob: "03/03/1990",
        image: "https://randomuser.me/api/portraits/men/3.jpg"
    }
];

const PatientList = () => {
    const [patients, setPatients] = useState([]);
    const [filteredPatients, setFilteredPatients] = useState([]);
    const [selectedPatient, setSelectedPatient] = useState(null);
    const [searchTerm, setSearchTerm] = useState('');

    useEffect(() => {
        // Load patients from local storage or an API
        const savedPatients = JSON.parse(localStorage.getItem('patients')) || [];
        setPatients(savedPatients);
        setFilteredPatients(savedPatients);
    }, []);

    const handleSearch = (event) => {
        setSearchTerm(event.target.value);
        const filtered = patients.filter(
            (patient) =>
                patient.name.toLowerCase().includes(event.target.value.toLowerCase())
        );
        setFilteredPatients(filtered);
    };

    const handleEdit = (patient) => {
        setSelectedPatient(patient);
    };

    const handleSave = (updatedPatient) => {
        const index = patients.findIndex(
            (patient) => patient.id === updatedPatient.id
        );
        const updatedPatients = [...patients];
        updatedPatients[index] = updatedPatient;
        setPatients(updatedPatients);
        setFilteredPatients(updatedPatients);
        localStorage.setItem('patients', JSON.stringify(updatedPatients));
        setSelectedPatient(null);
    };

    return (
        <div>
            <input type="text" value={searchTerm} onChange={handleSearch} />
            <ul>
                {filteredPatients.map((patient) => (
                    <li key={patient.id}>
                        <span>{patient.name}</span>
                        <button onClick={() => handleEdit(patient)}>Edit</button>
                    </li>
                ))}
            </ul>
            {selectedPatient && (
                <PatientModal patient={selectedPatient} onSave={handleSave} />
            )}
        </div>  
    );
};

export default PatientList;
