import React, {useState, useEffect} from "react";
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import {Button, Stack} from "@mui/material";
// import {patientData} from "./Data";
import Typography from '@mui/material/Typography';
import TextField from '@mui/material/TextField';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import "./Patient.css"
// import {patientData} from "./Header";

const PatientList = () => {
    const [search, setSearch] = useState("");
    const [filteredData, setFilteredData] = useState(datnewDataa);
    const [showModal, setShowModal] = useState(false);
    const [editingPatient, setEditingPatient] = useState(null);
    const [newPatientData, setNewPatientData] = useState({
        name: "",
        gender: "",
        dob: "",
        image: ""
    });
    const [open, setOpen]=useState(false)
    useEffect(() => {
        const results = data.filter(patient =>
            patient.name.toLowerCase().includes(search.toLowerCase())
        );
        setFilteredData(results);
    }, [search]);

    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    const handleImageChange = (event) => {
        const file = event.target.files[0];
        const reader = new FileReader();
        reader.readAsDataURL(file);
        reader.onload = () => {
            setNewPatientData(prevState => ({
                ...prevState,
                image: reader.result
            }));
        };
    };


    const handleSearch = event => {
        setSearch(event.target.value);
    };

    const handleEdit = patient => {
        setEditingPatient(patient);
        setNewPatientData(patient);
        setShowModal(true);
    };

    const handleSave = () => {
        if (editingPatient) {
            // Edit existing patient
            const updatedData = data.map(patient =>
                patient.id === editingPatient.id ? newPatientData : patient
            );
            setFilteredData(updatedData);
            localStorage.setItem("patientData", JSON.stringify(updatedData));
        } else {
            // Add new patient
            const newPatient = {
                id: data.length + 1,
                ...newPatientData
            };
            const updatedData = [...data, newPatient];
            setFilteredData(updatedData);
            localStorage.setItem("patientData", JSON.stringify(updatedData));
        }
        setShowModal(false);
        setEditingPatient(null);
        setNewPatientData({
            name: "",
            gender: "",
            dob: "",
            image: ""
        });
    };

    const handleCancel = () => {
        setShowModal(false);
        setEditingPatient(null);
        setNewPatientData({
            name: "",
            gender: "",
            dob: "",
            image: ""
        });
    };

    const handleInputChange = event => {
        const {name, value} = event.target;
        setNewPatientData(prevData => ({
            ...prevData,
            [name]: value
        }));
    };



    return (
        <div className="patient-list-body">
            <Stack direction={""} justifyContent="center" spacing={8}>
                <p style={{fontSize: "35px"}}>Find your patient here</p>
            </Stack>
            <div style={{display: 'flex', justifyContent: 'center', marginBottom: "100px", flexDirection: "row"}}>

                <input style={{width: "600px", height: "65px"}} type="text" value={search} onChange={handleSearch} />
            </div>
            <Box
                sx={{
                    display: 'flex',
                    flexWrap: 'wrap',
                    '& > :not(style)': {
                        m: 5,
                        width: 330,
                        // height: auto,
                    },
                }}
            >
                {filteredData.map((patient) => (

                    <Paper  >
                        <div style={{width: "100%", height: "200px"}}>
                            <img style={{width: "100%", height: "100%"}} src={patient.image} alt="green iguana" />
                        </div>
                        <Typography gutterTop variant="h5" marginLeft={"20px"}>
                            {patient.name}
                        </Typography>
                        <Stack direction={"column"} marginLeft={"20px"}>
                            <p>{patient.gender}</p>
                            <p>{patient.dob}</p>
                        </Stack>
                        <div style={{display: 'flex', justifyContent: 'center', marginBottom: "30px", }}>

                            <Button style={{width: "200px", backgroundColor: "black"}} variant="contained" onClick={() => {handleEdit(patient);setOpen(true);}
}>
                                Edit
                            </Button>
                        </div>
                    </Paper>

                ))}
            </Box>


            {showModal && (
                <div>
                    {/* <h2>{editingPatient ? "Edit Patient" : "Add New Patient"}</h2> */}
                    <Dialog open={open} onClose={handleClose} >
                        <DialogTitle></DialogTitle>
                        <DialogContent>
                            <DialogContentText>
                                Edit your patient details here
                            </DialogContentText>
                            <input type="file" accept="image/*" onChange={handleImageChange} style={{marginBottom: '20px'}} />

                            <img src={newPatientData.image} alt="patient image" style={{width: '100%', height: 'auto', marginBottom: '20px'}} />

                            <TextField
                                autoFocus
                                margin="dense"
                                type="email"
                                fullWidth
                                variant="standard"
                                id="name" name="name" value={newPatientData.name} onChange={handleInputChange}
                            />
                            <TextField
                                autoFocus
                                margin="dense"
                                type="email"
                                fullWidth
                                variant="standard"
                                sx={{marginBlock: "30px"}}
                                id="name" name="dob" value={newPatientData.dob} onChange={handleInputChange}
                            />
                            <TextField
                                autoFocus
                                margin="dense"
                                type="email"
                                fullWidth
                                variant="standard"
                                id="name" name="gender" value={newPatientData.gender} onChange={handleInputChange}
                            />
                        </DialogContent>
                        <DialogActions style={{display: 'flex', justifyContent: 'center', marginBottom: "30px", }}>
                            <Button style={{width: "200px", backgroundColor: "black"}} variant="contained" onClick={handleSave}>Save</Button>
                            <Button style={{width: "200px", backgroundColor: "black"}} variant="contained" onClick={handleClose}>Cancel</Button>
                        </DialogActions>
                    </Dialog>

                </div>
            )}
        </div>
    );
}

export default PatientList;
