import React, {useState, useEffect} from "react";
import Grid from '@mui/material/Grid';
import FormLabel from '@mui/material/FormLabel';
import FormControl from '@mui/material/FormControl';
import FormControlLabel from '@mui/material/FormControlLabel';
// import HighlightedCode from 'docs/src/modules/components/HighlightedCode';
import RadioGroup from '@mui/material/RadioGroup';
import Radio from '@mui/material/Radio';
import Paper from '@mui/material/Paper';
import {Button} from "@mui/material";
import {patientData} from "./Data";

const PatientList = () => {
    const [search, setSearch] = useState("");
    const [filteredData, setFilteredData] = useState(patientData);
    const [showModal, setShowModal] = useState(false);
    const [editingPatient, setEditingPatient] = useState(null);
    const [newPatientData, setNewPatientData] = useState({
        name: "",
        gender: "",
        dob: "",
        image: ""
    });

    useEffect(() => {
        const results = patientData.filter(patient =>
            patient.name.toLowerCase().includes(search.toLowerCase())
        );
        setFilteredData(results);
    }, [search]);

    const handleSearch = event => {
        setSearch(event.target.value);
    };

    const handleEdit = patient => {
        setEditingPatient(patient);
        setNewPatientData(patient);
        setShowModal(true);
    };

    const handleSave = () => {
        if (editingPatient) {
            // Edit existing patient
            const updatedData = patientData.map(patient =>
                patient.id === editingPatient.id ? newPatientData : patient
            );
            setFilteredData(updatedData);
            localStorage.setItem("patientData", JSON.stringify(updatedData));
        } else {
            // Add new patient
            const newPatient = {
                id: patientData.length + 1,
                ...newPatientData
            };
            const updatedData = [...patientData, newPatient];
            setFilteredData(updatedData);
            localStorage.setItem("patientData", JSON.stringify(updatedData));
        }
        setShowModal(false);
        setEditingPatient(null);
        setNewPatientData({
            name: "",
            gender: "",
            dob: "",
            image: ""
        });
    };

    const handleCancel = () => {
        setShowModal(false);
        setEditingPatient(null);
        setNewPatientData({
            name: "",
            gender: "",
            dob: "",
            image: ""
        });
    };

    const handleInputChange = event => {
        const {name, value} = event.target;
        setNewPatientData(prevData => ({
            ...prevData,
            [name]: value
        }));
    };

    return (
        <div>
            <Grid sx={{flexGrow: 1}} container spacing={2}>
                <Grid item xs={12}>
                    <Grid container justifyContent="center" spacing={spacing}>
                        {[0, 1, 2].map((value) => (
                            <Grid key={value} item>
                                <Paper
                                    sx={{
                                        height: 140,
                                        width: 100,
                                        backgroundColor: (theme) =>
                                            theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
                                    }}
                                />
                            </Grid>
                        ))}
                    </Grid>
                </Grid>
                <Grid item xs={12}>
                    <Paper sx={{p: 2}}>
                        <Grid container>
                            <Grid item>
                                <FormControl component="fieldset">
                                    <FormLabel component="legend">spacing</FormLabel>
                                    <RadioGroup
                                        name="spacing"
                                        aria-label="spacing"
                                        value={spacing.toString()}
                                        onChange={handleChange}
                                        row
                                    >
                                        {[0, 0.5, 1, 2, 3, 4, 8, 12].map((value) => (
                                            <FormControlLabel
                                                key={value}
                                                value={value.toString()}
                                                control={<Radio />}
                                                label={value.toString()}
                                            />
                                        ))}
                                    </RadioGroup>
                                </FormControl>
                            </Grid>
                        </Grid>
                    </Paper>
                    {/* <HighlightedCode code={jsx} language="jsx" /> */}
                </Grid>
            </Grid>
            <input type="text" value={search} onChange={handleSearch} />
            <ul>
                {filteredData.map(patient => (
                    <li key={patient.id}>
                        <img src={patient.image} alt={patient.name} />
                        <p>{patient.name}</p>
                        <p>{patient.dob}</p>
                        <p>{patient.gender}</p>
                        <button onClick={() => handleEdit(patient)}>Edit</button>
                    </li>
                ))}
            </ul>
            {showModal && (
                <div className="modal">
                    <div className="modal-content">
                        <h2>{editingPatient ? "Edit Patient" : "Add New Patient"}</h2>
                        <form>
                            <label htmlFor="name">Name:</label>
                            <input type="text" id="name" name="name" value={newPatientData.name} onChange={handleInputChange} />
                            <label htmlFor="gender">Gender:</label>
                            <input type="text" id="gender" name="gender" value={newPatientData.gender} onChange={handleInputChange} />
                            <label htmlFor="dob">Date of Birth:</label>
                            <input type="text" id="dob" name="dob" value={newPatientData.dob} onChange={handleInputChange} />
                            <label htmlFor="image">Image URL:</label>
                            <input type="text" id="image" name="image" value={newPatientData.image} onChange={handleInputChange} />
                        </form>
                        <Button onClick={handleSave}>Save</Button>
                        <Button onClick={handleCancel}>cancel</Button>
                    </div>
                </div>
            )}
        </div>
    );
}

export default PatientList;
